import Layout from '../../components/MyLayout'
import 'isomorphic-unfetch'
import Link from 'next/link'
import React from 'react'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import { Helmet } from 'react-helmet';
import {
    Container,
    Divider,
    Dropdown,
    Grid,
    Header,
    Image,
    List,
    Menu,
    Icon,
    Segment,
    Pagination
} from 'semantic-ui-react';
import { API_URL } from '../../config/config'

export default class Update extends React.Component {
    static async getInitialProps(context) {
        // eslint-disable-next-line no-undef
        const { page } = context.query
        const res = await fetch(`${API_URL}update?page=${page}`)
        const json = await res.json()
        // console.log(json)
        return { data: json, page: page }
    }
    handleChangepage =(e,data) => {
        console.log(data)
    }
    render() {
        console.log(this.props)
        return (
            <Layout>
                <style>
                    {`
      html, body {
        background-color: #242933 !important;
      }
      p {
        align-content: center;
        background-color: #495285;
        color: #fff;
        display: flex;
        flex-direction: column;
        justify-content: center;
        min-height: 6em;
      }
      p > span {
        opacity: 0.4;
        text-align: center;
      }
    }
    `}
                </style>
                <Helmet>
                    <title>Đọc truyện online - YYtruyen.com</title>
                    <meta name="description" content="Đọc truyện miễn phí, cập nhật nhanh nhất các truyện ngôn tình, truyện kiếm hiệp, xuyên không, sủng, ngược và nhiều thể loại khác" />
                    <meta name="theme-color" content="#008f68" />
                    <meta name="keywords" content="đọc truyện, đọc truyện online, truyện ngôn tình, truyện kiếm hiệp, xuyên không" />
                </Helmet>
                <Container style={{ marginTop: '7em' }}>
                    <Grid>
                        <Grid.Column mobile={16} tablet={16} computer={16}>
                            <Header className='title-name' as='h2'>Lastest Updates</Header>
                        </Grid.Column>
                        {this.props.data.data.length !== 0 ?
                            this.props.data.data.map(mangalist => (
                                <Grid.Column mobile={8} tablet={3} computer={3}>
                                    <Grid verticalAlign='top'>
                                        <Grid.Column mobile={16} tablet={16} computer={16}>
                                            <Link
                                                as={`/truyen/${mangalist.idnovel}`} href={`/truyen/[id]`}
                                            // href={'/truyen/' + mangalist.idnovel}
                                            >
                                                <Image fluid label={{
                                                    color: 'orange',
                                                    content: `View: ${mangalist.view}`,
                                                    ribbon: true,
                                                }} className='item-img' src={mangalist.cover} style={{ height: 240 }} />
                                            </Link>

                                        </Grid.Column>
                                        <Grid.Column mobile={16} tablet={16} computer={16} >
                                            <Header className='book-name' as='h4'>{mangalist.novelsname}</Header>
                                            <Header className='book-catalog' size='tiny' as='h6'>Last chapter: {mangalist.lasterchapter}</Header>
                                        </Grid.Column>
                                    </Grid>
                                </Grid.Column>
                            )) : null}
                        <Grid.Column mobile={16} tablet={16} computer={16}>
                            <Pagination onPageChange={(e,data) => this.handleChangepage(e,data)} defaultActivePage={this.props.page} totalPages={this.props.data.totalpage} />
                        </Grid.Column>
                    </Grid>

                </Container>

            </Layout>
        )
    }
}
