Truyen.getInitialProps = async context => {
  // eslint-disable-next-line no-undef
  const {
    id,
    page
  } = context.query;
  const res = await fetch(`${API_URL}novel?id=${id}&page=${page}`);
  const json = await res.json(); // console.log(json)

  return {
    data: json,
    page: page,
    id: id
  };
};

