import React from 'react'
import Link from 'next/link'
import 'isomorphic-unfetch'
import { Helmet } from 'react-helmet';
import Layout from '../components/MyLayout'
import Image from 'next/image'
import {
  Container,
  Divider,
  Dropdown,
  Grid,
  Header,
  List,
  Menu,
  Icon,
  Segment,
} from 'semantic-ui-react';
import { API_URL } from '../config/config'
export default class Index extends React.Component {
  static async getInitialProps() {
    // eslint-disable-next-line no-undef
    const res = await fetch(`${API_URL}update?page=1`)
    const json = await res.json()
    // console.log(json.data)
    return { mangalist: json.data }
  }

  render() {
    return (
      <Layout>
        <style>
          {`
      html, body {
        background-color: #242933 !important;
      }
      p {
        align-content: center;
        background-color: #495285;
        color: #fff;
        display: flex;
        flex-direction: column;
        justify-content: center;
        min-height: 6em;
      }
      p > span {
        opacity: 0.4;
        text-align: center;
      }
    }
    `}
        </style>
        <Helmet>
          <title>KitesNovel - Huge Web Novel Community for Reading</title>
          <meta name="description" content="A Huge Web Novel Community incloud Chinese Novels ( Translated ) , Original Novels, Light Novels and more. You can read all of them Online or Offline. Popular Novel Updates Daily With Fast Translation Speed And High Quality. We provide all genres of  web novel. e.g. love story, historical, romance, fantasy, wuxia, xianxia, light novel and another novels" />
          <meta name="theme-color" content="#008f68" />
          <meta name="keywords" content="Chinese novel,novel updates,web novel,novel online,read novels online,light novel,novel translation,wuxia" />
        </Helmet>
        <Container style={{ marginTop: '7em' }}>
          <Grid>
            <Grid.Column mobile={16} tablet={16} computer={16}>
              <Header className='title-name' as='h2'>Lastest Updates</Header>
            </Grid.Column>
            {this.props.mangalist.length !== 0 ?
              this.props.mangalist.map(mangalist => (
                <Grid.Column mobile={8} tablet={3} computer={3}>
                  <Grid verticalAlign='top'>
                    <Grid.Column mobile={16} tablet={16} computer={16}>
                      <Link
                        as={`/novel/${mangalist.idnovel}`} href={`/novel/[id]`}
                      // href={'/truyen/' + mangalist.idnovel}
                      >
                        <Image
                          className='item-img'
                          src={mangalist.cover}
                          alt={mangalist.novelsname}
                          width={200}
                          height={240}
                        />
                        {/* <Image fluid label={{
                          color: 'orange',
                          content: `View: ${mangalist.view}`,
                          ribbon: true,
                        }} className='item-img' src={mangalist.cover} style={{ height: 240 }} /> */}
                      </Link>

                    </Grid.Column>
                    <Grid.Column mobile={16} tablet={16} computer={16} >
                      <Header className='book-name' as='h4'>{mangalist.novelsname}</Header>
                      <Header className='book-catalog' size='tiny' as='h6'>Last chapter: {mangalist.lasterchapter}</Header>
                    </Grid.Column>
                  </Grid>
                </Grid.Column>
              )) : null}
          </Grid>
        </Container>

      </Layout>
    )
  }
}
