import Layout from '../../../components/MyLayout'
import 'isomorphic-unfetch'
import Link from 'next/link'
import React, { Component, createRef } from 'react';
import { createMedia } from '@artsy/fresnel'
import { connect } from 'react-redux';
import PropTypes from 'prop-types'
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import { Helmet } from 'react-helmet';
import { saveNovel, settingNovel } from '../../../redux/actions/novelActions'
import { API_URL } from '../../../config/config'
import {
    Container, Breadcrumb,
    Divider,
    Dropdown,
    Grid,
    Header,
    Icon,
    Image,
    List,
    Menu,
    Segment,
    Visibility,
    Item,
    Sticky, Rail,
    Ref, Button
} from 'semantic-ui-react';
const { MediaContextProvider, Media } = createMedia({
    breakpoints: {
        mobile: 0,
        tablet: 768,
        computer: 1024,
    },
})
const menuStyle = {
    border: 'none',
    borderRadius: 0,
    boxShadow: 'none',
    marginBottom: '1em',
    transition: 'box-shadow 0.5s ease, padding 0.5s ease',
}

const fixedMenuStyle = {
    backgroundColor: '#fff',
    border: '1px solid #ddd',
    boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)',
}
class Chapter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            prechap: '',
            nextchap: '',
            menuFixed: false,
            overlayFixed: false,
            totalchapter: 1
        }
    }
    contextRef = createRef()
    handleOverlayRef = (c) => {
        const { overlayRect } = this.state

        if (!overlayRect) {
            this.setState({ overlayRect: _.pick(c.getBoundingClientRect(), 'height', 'width') })
        }
    }

    stickOverlay = () => this.setState({ overlayFixed: true })

    stickTopMenu = () => this.setState({ menuFixed: true })

    unStickOverlay = () => this.setState({ overlayFixed: false })

    unStickTopMenu = () => this.setState({ menuFixed: false })
    static async getInitialProps(context) {
        // eslint-disable-next-line no-undef
        const { id, chapter } = context.query
        const res = await fetch(`${API_URL}chapter?novelid=${id}&chapterid=${chapter}`)
        // const res1 = await fetch(`http://localhost:8001/novel?id=${id}`)
        const json = await res.json()
        // const json1 = await res1.json();
        // let prechap = '', nextchap = ''
        // if (json1.chapterlist.length === 1) {
        //     return { novelchapter: json, prechap: '', nextchap: '' }
        // } else {
        //     for (let i = 0; i < json1.chapterlist.length; i++) {
        //         if (
        //             json.idchapter ===
        //             json1.chapterlist[i].idchapter
        //         ) {
        //             if (i === 0) {
        //                 return { novelchapter: json, prechap: '', nextchap: json1.chapterlist[i + 1].idchapter }

        //             }
        //             if (i > 0 && i < json1.chapterlist.length - 1) {
        //                 return { novelchapter: json, prechap: json1.chapterlist[i - 1].idchapter, nextchap: json1.chapterlist[i + 1].idchapter }
        //             }
        //             if (i === json1.chapterlist.length - 1) {
        //                 return { novelchapter: json, prechap: json1.chapterlist[i - 1].idchapter, nextchap: '' }
        //             }
        //         }
        //     }
        // }
        // console.log(json)
        return { novelchapter: json }
    }
    componentDidUpdate(prevProps, prevState) {
        if (this.props.novelchapter !== prevProps.novelchapter) {
            fetch(`${API_URL}novel?id=${this.props.novelchapter.idnovels}`)
                .then(r => r.json())
                .then(data => {
                    if (data.chapterlist.length === 1) {
                        this.state({ prechap: '', nextchap: '', totalchapter: data.chapterlist.length })
                    } else {
                        for (let i = 0; i < data.chapterlist.length; i++) {
                            if (
                                this.props.novelchapter.idchapter ===
                                data.chapterlist[i].idchapter
                            ) {
                                if (i === 0) {
                                    this.setState({ prechap: '', nextchap: data.chapterlist[i + 1].idchapter, totalchapter: data.chapterlist.length })
                                    // return { novelchapter: json, prechap: '', nextchap: json1.chapterlist[i + 1].idchapter }

                                }
                                if (i > 0 && i < data.chapterlist.length - 1) {
                                    this.setState({ prechap: data.chapterlist[i - 1].idchapter, nextchap: data.chapterlist[i + 1].idchapter, totalchapter: data.chapterlist.length })
                                    // return { novelchapter: json, prechap: json1.chapterlist[i - 1].idchapter, nextchap: json1.chapterlist[i + 1].idchapter }
                                }
                                if (i === data.chapterlist.length - 1) {
                                    this.setState({ prechap: data.chapterlist[i - 1].idchapter, nextchap: '', totalchapter: data.chapterlist.length })
                                    // return { novelchapter: json, prechap: json1.chapterlist[i - 1].idchapter, nextchap: '' }
                                }
                            }
                        }
                    }
                });
        }
        if (this.props.novelReducer !== prevProps.novelReducer) {
            this.setState({
                nightmode: this.props.novelReducer.nightmode,
                fontsize: this.props.novelReducer.fontsize,
                font: this.props.novelReducer.font,
                lineheight: this.props.novelReducer.lineheight,
                background: this.props.novelReducer.background,
            })
        }
    }

    componentDidMount() {
        fetch(`${API_URL}novel?id=${this.props.novelchapter.idnovels}`)
            .then(r => r.json())
            .then(data => {
                this.props.saveNovel(data)
                if (data.chapterlist.length === 1) {
                    this.state({ prechap: '', nextchap: '' })
                } else {
                    for (let i = 0; i < data.chapterlist.length; i++) {
                        if (
                            this.props.novelchapter.idchapter ===
                            data.chapterlist[i].idchapter
                        ) {
                            if (i === 0) {
                                this.setState({ prechap: '', nextchap: data.chapterlist[i + 1].idchapter })
                                // return { novelchapter: json, prechap: '', nextchap: json1.chapterlist[i + 1].idchapter }

                            }
                            if (i > 0 && i < data.chapterlist.length - 1) {
                                this.setState({ prechap: data.chapterlist[i - 1].idchapter, nextchap: data.chapterlist[i + 1].idchapter })
                                // return { novelchapter: json, prechap: json1.chapterlist[i - 1].idchapter, nextchap: json1.chapterlist[i + 1].idchapter }
                            }
                            if (i === data.chapterlist.length - 1) {
                                this.setState({ prechap: data.chapterlist[i - 1].idchapter, nextchap: '' })
                                // return { novelchapter: json, prechap: json1.chapterlist[i - 1].idchapter, nextchap: '' }
                            }
                        }
                    }
                }
            });
    }
    handleNightMode = (data) => {
        let params = {
            nightmode: !data,
            fontsize: this.state.fontsize,
            font: this.state.font,
            lineheight: this.state.lineheight,
            background: this.state.background,
        }
        this.props.settingNovel(params)
    }
    render() {
        console.log(this.props)
        console.log(this.state)
        const { menuFixed, overlayFixed, overlayRect } = this.state;
        return (
            <Layout>
                <style>
                    {`
          html, body {
            background: ${this.props.novelReducer.nightmode === true ? '#1a1a1a' : '#f5f7fa'} ;
          }
        `}
                </style>
                <Helmet>
                    <title>{`Read ${this.props.novelchapter.chaptername}`} - KitesNovel</title>
                    <meta name="description" content={`Read ${this.props.novelchapter.chaptername} for free, total ${this.state.totalchapter} chapter `} />
                    <meta name="theme-color" content="#008f68" />
                    <meta name="keywords" content={`${this.props.novelchapter.chaptername}, ${this.props.novelchapter.chaptername} free, ${this.props.novelReducer.novelname} light novel, ${this.props.novelReducer.novelname} full chapters, ${this.props.novelReducer.novelname} latest chapters`} />
                </Helmet>
                <MediaContextProvider>
                    <Media greaterThan='mobile'>
                        <Visibility
                            onBottomPassed={this.stickTopMenu}
                            onBottomVisible={this.unStickTopMenu}
                            once={false}
                        >
                            <Menu
                                borderless
                                fixed={menuFixed ? 'top' : undefined}
                                style={menuFixed ? fixedMenuStyle : menuStyle}
                            >
                                <Container>
                                    <Menu.Item>
                                        <Link href='/'>
                                            <Image size='tiny' src='/logo.png' />
                                        </Link>

                                    </Menu.Item>
                                    <Menu.Item header>
                                        <Breadcrumb size={'large'}>
                                            <Breadcrumb.Section link><Link href='/'>Home</Link></Breadcrumb.Section>
                                            <Breadcrumb.Divider icon='right chevron' />
                                            <Breadcrumb.Section link>
                                                <Link
                                                    as={`/novel/${this.props.novelchapter.idnovels}`} href={`/novel/[id]`}
                                                >{this.props.novelReducer.novelname}
                                                </Link></Breadcrumb.Section>
                                            <Breadcrumb.Divider icon='right chevron' />
                                            <Breadcrumb.Section active>{this.props.novelchapter.chaptername}</Breadcrumb.Section>
                                        </Breadcrumb>
                                    </Menu.Item>
                                </Container>
                            </Menu>
                        </Visibility>
                    </Media>
                    <Media at='mobile'>
                        <Visibility
                            onBottomPassed={this.stickTopMenu}
                            onBottomVisible={this.unStickTopMenu}
                            once={false}
                        >
                            <Menu
                                borderless
                                fixed={menuFixed ? 'top' : undefined}
                                style={menuFixed ? fixedMenuStyle : menuStyle}
                            >
                                <Container>
                                    <Menu.Item>
                                        <Link href='/'>
                                            <Image size='tiny' src='/logo.png' />
                                        </Link>
                                    </Menu.Item>
                                    <Menu.Item header>
                                        <Breadcrumb size={'large'}>
                                            <Breadcrumb.Section link>
                                                <Link
                                                    as={`/novel/${this.props.novelchapter.idnovels}`} href={`/novel/[id]`}
                                                >{this.props.novelReducer.novelname}
                                                </Link></Breadcrumb.Section>
                                        </Breadcrumb>
                                    </Menu.Item>
                                </Container>
                            </Menu>
                        </Visibility>
                    </Media>
                </MediaContextProvider>
                <Container text style={{ backgroundColor: this.props.novelReducer.nightmode === true ? '#232323' : this.props.novelReducer.background }}>
                    <Grid style={{ marginTop: 20 }}>
                        <Grid.Column className='detail-containers' mobile={16} tablet={16} computer={16}>
                            <Ref innerRef={this.contextRef}>
                                <Segment style={{ backgroundColor: this.props.novelReducer.nightmode === true ? '#232323' : this.props.novelReducer.background }}>
                                    <Item.Group textAlign='left'>
                                        <Item.Description className='content' style={{
                                            fontFamily: this.props.novelReducer.font, lineHeight: this.props.novelReducer.lineheight,
                                            fontSize: this.props.novelReducer.fontsize, color: this.props.novelReducer.nightmode === true ? '#b3b3b3' : 'black'
                                        }} >
                                            {this.props.novelchapter.content !== undefined ?
                                                ReactHtmlParser(this.props.novelchapter.content)
                                                : null
                                            }
                                        </Item.Description>
                                    </Item.Group>
                                    <MediaContextProvider>
                                        <Media greaterThan='mobile'>
                                            <Rail position='right' style={{ marginTop: 100 }}>
                                                <Sticky offset={100} bottomOffset={100} context={this.contextRef}>
                                                    <List>
                                                        <List.Item>
                                                            <Button size='large' circular icon='bars' />
                                                        </List.Item>
                                                        <List.Item>
                                                            <Button size='large' circular icon='font' />
                                                        </List.Item>
                                                        <List.Item>
                                                            <Button onClick={() => this.handleNightMode(this.props.novelReducer.nightmode)} size='large' circular icon={this.props.novelReducer.nightmode ? 'lightbulb outline' : 'moon'} />
                                                        </List.Item>
                                                    </List>
                                                </Sticky>
                                            </Rail>
                                        </Media>
                                    </MediaContextProvider>
                                </Segment>
                            </Ref>
                        </Grid.Column>
                        <MediaContextProvider>
                            <Media greaterThan='mobile'>
                                <Grid.Row verticalAlign='middle'>
                                    <Button.Group widths='3'>
                                        <Link
                                            as={`/doc/${this.props.novelchapter.idnovels}/${this.state.prechap}`}
                                            href={`/doc/[id]/[chapter]`}
                                        >
                                            <Button size='huge' color='black' disabled={this.state.prechap !== "" ? false : true} labelPosition='left' icon='left chevron' content='Prev chapter' />
                                        </Link>

                                        <Button size='huge' color='black' icon='bars' content='Chapter List' />
                                        <Link
                                            as={`/doc/${this.props.novelchapter.idnovels}/${this.state.nextchap}`}
                                            href={`/doc/[id]/[chapter]`}
                                        >
                                            <Button size='huge' color='black' disabled={this.state.nextchap !== "" ? false : true} labelPosition='right' icon='right chevron' content='Next chapter' />
                                        </Link>
                                    </Button.Group>
                                </Grid.Row>
                            </Media>
                            <Media at='mobile'>
                                <Grid.Row mobile={16} tablet={16} computer={16} verticalAlign='middle'>
                                    <Button.Group widths='3'>
                                        <Link
                                            as={`/doc/${this.props.novelchapter.idnovels}/${this.state.prechap}`}
                                            href={`/doc/[id]/[chapter]`}
                                        >
                                            <Button size='huge' color='black' disabled={this.state.prechap !== "" ? false : true} labelPosition='left' icon='left chevron' />
                                        </Link>

                                        <Button size='huge' color='black' icon='bars' />
                                        <Link
                                            as={`/doc/${this.props.novelchapter.idnovels}/${this.state.nextchap}`}
                                            href={`/doc/[id]/[chapter]`}
                                        >
                                            <Button size='huge' color='black' disabled={this.state.nextchap !== "" ? false : true} labelPosition='right' icon='right chevron' />
                                        </Link>
                                    </Button.Group>
                                </Grid.Row>
                            </Media>
                        </MediaContextProvider>
                    </Grid>
                </Container>

            </Layout>
        )
    }
}
function mapStateToProps(state) {
    return {
        novelReducer: state.novelReducer
    };
}

export default connect(mapStateToProps, { saveNovel, settingNovel })(Chapter);
