const initialState = {
    isLoading: false,
    error: false,
    data: [],
};
const novelReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'INCREMENT_COUNTER':
            return { ...state, isLoading: true, error: false, data: [], success: null };
        case 'DECREMENT_COUNTER':
            return {
                ...state,
                isLoading: false,
                error: false,
                data: action.payload,
            };

        case 'FETCH_ERROR':
            return { ...state, isLoading: false, error: true, data: [] };
        default:
            return state;
    }
};

export default  novelReducer ;
