import 'isomorphic-unfetch'
import Link from 'next/link'
import React from 'react'
import { connect } from 'react-redux';
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import { Helmet } from 'react-helmet';
import Layout from '../../components/MyLayout'
import { useRouter } from 'next/router';
import { saveNovel } from '../../redux/actions/novelActions'
import {
    Button,
    Container,
    Divider,
    Grid,
    Header,
    Icon,
    Image,
    List,
    Menu,
    Segment,
    Sidebar,
    Visibility,
    Item,
    Label,
    Tab
} from 'semantic-ui-react';

class Truyen extends React.Component {
    static getInitialProps({ store }) { }

    constructor(props) {
        super(props);
    }
    componentDidMount() {
        console.log(this.props.data)
    }
    
    static async getInitialProps({ query: { id } }) {
        // eslint-disable-next-line no-undef
        // const { id } = context.query
        // const router = useRouter()
        // const { id } = router.query
        const res = await fetch(`http://localhost:8001/novel?id=${id}`)
        const json = await res.json()
        // console.log(json)

        return { data: json }
    }
    
    render() {
        console.log(this.props)
        
        return (
            <Layout>
                <style>
                    {`
      html, body {
        background-color: #f2f3f5 !important;
      }
    }
    `}
                </style>
                <Helmet>
                    <title>{`${this.props.data.novelsname}`} - YYtruyen</title>
                    <meta name="description" content={`Truyện ${this.props.data.novelsname} là tác phẩm của tác giả ${this.props.data.author}, tiến trình: ${this.props.data.status}. Vào yytruyen.com để đọc những chương mới nhất của tác phẩm này nhé  `} />
                    <meta name="theme-color" content="#008f68" />
                    <meta name="keywords" content="đọc truyện online, truyện online, truyện ngôn tình, truyện kiếm hiệp, truyện ngược, sủng" />
                    <meta property="og:title" content={`${this.props.data.novelsname}`} />
                    <meta property="og:type" content="book" />
                    <meta property="og:image" content={`${this.props.data.cover}`} />
                    <meta property="og:description" content={`${this.props.data.des}`} />
                    <meta property="og:site_name" content="Yytruyen.com" />
                    <meta property="og:url" content={`http://yytruyen.com/truyen/${this.props.data.idnovels}`} />
                    <meta property="book:author" content={`${this.props.data.author}`} />
                    <meta property="og:image:width" content="200" />
                    <meta property="og:image:height" content="300" />

                    <script type="application/ld+json">{`
                    {
                    "@context": "http://schema.org",
                    "url": "http://yytruyen.com/truyen/${this.props.data.idnovels}",
                    "image": "${this.props.data.cover}",
                    "@type": "Book",
                    "name": "${this.props.data.novelsname}",
                    "author": "${this.props.data.author}",
                    "description": "${this.props.data.des}"
                    }
                    `}</script>
                </Helmet>
                <Segment
                    inverted
                    style={{ maxHeight: '32.625rem', padding: '1em 0em' }}
                    vertical
                >
                    <div className='book-info'>
                        <img className='glass_background-img' src={this.props.data.cover} />
                        <Container style={{}}>
                            <Grid>
                                <Grid.Column mobile={16} tablet={4} computer={4}>
                                    <Image fluid className='item-img' src={this.props.data.cover} style={{ height: '22.75rem' }} />
                                </Grid.Column>
                                <Grid.Column mobile={16} tablet={12} computer={12}>
                                    <Item.Group textAlign='left'>
                                        <Item.Content>
                                            <Item.Header className='book-title' as='h2'>{this.props.data.novelsname}</Item.Header>
                                        </Item.Content>
                                        <Item.Extra textAlign='left' style={{ marginTop: 10 }}>
                                            <span className='label'>Author:</span> {this.props.data.author}
                                        </Item.Extra>
                                        <Item.Extra textAlign='left' style={{ marginTop: 10 }}>
                                            Genres: {this.props.data.genresdata.map(genresdata => (
                                            <Link as={`/theloai/${genresdata.idgenres}/1`} href={`/theloai/[id]/[page]`}>
                                                <Label as='a' color='yellow'>
                                                    <Label.Detail>{genresdata.genrename}</Label.Detail>
                                                </Label>

                                            </Link>
                                        ))}
                                        </Item.Extra>
                                        <Item.Extra textAlign='left' style={{ marginTop: 10 }}>
                                            <span className='label'>Status:</span> {this.props.data.status}
                                        </Item.Extra>
                                    </Item.Group>
                                    <Button size='huge' color='yellow'>READ</Button>
                                </Grid.Column>
                            </Grid>
                        </Container>
                    </div>
                </Segment>
                <Container>
                    <Grid>
                        <Grid.Column className='detail-container' mobile={16} tablet={16} computer={16}>
                            <Divider horizontal>
                                <Header className='title-name-bottom' as='h2'>Synopsis</Header>
                            </Divider>

                            <Item.Group textAlign='left'>
                                <Item.Description className='content'>
                                    {ReactHtmlParser(this.props.data.description)}
                                </Item.Description>
                            </Item.Group>
                            <Divider horizontal>
                                <Header className='title-name-bottom' as='h2'>Chapter</Header>
                            </Divider>

                            <Grid>
                                {this.props.data.chapterlist.map((datachapter, key) => (
                                    <Grid.Column mobile={16} tablet={8} computer={8}>
                                        <Link
                                            as={`/doc/${this.props.data.idnovels}/${datachapter.idchapter}`}
                                            href={`/doc/[id]/[chapter]`}>
                                            <Header className='chapter' as='a'>{datachapter.chaptername}</Header>
                                        </Link>
                                        <Divider inverted></Divider>
                                    </Grid.Column>

                                ))}
                            </Grid>
                        </Grid.Column>
                    </Grid>
                </Container>


            </Layout>
        )
    }
}
function mapStateToProps(state) {
    return {
        novelReducer: state.novelReducer
    };
}

export default connect(mapStateToProps, { saveNovel })(Truyen);
